namespace Crucial\Service\Chargify;

class WebhookHandler
{
    /**
     * Path to logfile
     *
     * @var string|bool
     */
    private $logfile;

    /**
     * Initialize webhook handler
     *
     * @param string|false $logfile Path to logfile
     */
    public function __construct($logfile = false)
    {
        $this->logfile = $logfile;
    }

    /**
     * Get webhook from POST request
     * @see https://help.chargify.com/webhooks/webhooks-reference.html#webhook-contents
     *
     * @return array|false
     */
    public function get()
    {
        $webhook = $this->getWebhook();
        $this->logWebhook($webhook);
        return $webhook;
    }

    /**
     * Generate fake webhook
     *
     * @return array|false
     */
    public function getFake()
    {
        $webhook = $this->getFakeWebhook();
        $this->logWebhook($webhook);
        return $webhook;
    }

    /**
     * Get Chargify webhook from Post request
     * @see https://help.chargify.com/webhooks/webhooks-reference.html#webhook-contents
     *
     * @return array|false
     */
    private function getWebhook()
    {
        if (isset($_POST['event'])) {
            $webhook = [
                'event'     => $_POST['event'],
                'id'        => $_POST['id'],
                'payload'   => $_POST['payload'],
            ];

            return $webhook;
        } else {
            return false;
        }
    }

    /**
     * Log webhook data into logfile
     *
     * @param array|false $webhook
     */
    private function logWebhook($webhook = false)
    {
        if ($webhook && $this->logfile) {
            try {
                file_put_contents(
                    $this->logfile,
                    date("Y-m-d H:i:s: ") . print_r($webhook, true) . PHP_EOL,
                    FILE_APPEND
                );
            } catch (\Exception $ignore) {
            }
        }
    }

